var arr = input.split('');
console.log( arr );

//Question One
var netMoveOne = 0;
for(var i = 0; i < arr.length; i++){
    if(arr[i] == "("){
        netMoveOne++;
    }else if(arr[i] == ")"){
        netMoveOne--;
    }
}
if(netMoveOne > 0){
    console.log("The train is " + netMoveOne + " stations west");
}else if(netMoveOne < 0){
    console.log("The train is " + Math.abs(netMoveOne) + " stations east");
}

//Question 2
var netMoveTwo = 0;
for(var i = 0; i < arr.length; i++){
    if(arr[i] == "("){
        if(netMoveTwo < 0){
            netMoveTwo++;
        }
    }else if(arr[i] == ")"){
        netMoveTwo--;
    }
}
if(netMoveTwo > 0){
    console.log("The train is " + netMoveTwo + " stations west");
}else if(netMoveTwo < 0){
    console.log("The train is " + Math.abs(netMoveTwo) + " stations east");
}

//Question 3
var netMoveThree = 0;
for(var i = 0; i < arr.length; i++){
    if(arr[i] == "("){
        if(arr[i] == arr[i + 1]){
            netMoveThree++;
            i++;
        }else{
            netMoveThree++;
        }
    }else if(arr[i] == ")"){
        if(arr[i] == arr[i + 1]){
            netMoveThree--;
            i++;
        }else{
            netMoveThree--;
        }
    }
}
if(netMoveThree > 0){
    console.log("The train is " + netMoveThree + " stations west");
}else if(netMoveThree < 0){
    console.log("The train is " + Math.abs(netMoveThree) + " stations east");
}

//Question 4
function chunk(array, size) {
    const chunked_arr = [];
    let index = 0;
    while (index < array.length) {
        chunked_arr.push(array.slice(index, size + index));
        index += size;
    }
    return chunked_arr;
}
var fourArr = chunk(input, 4);

var netMoveFour = 0;
for(var i = 0; i < fourArr.length; i++){
    var currentMove = 0;
    if(fourArr[i][0] == "("){
        if(fourArr[i][1] == "("){
            currentMove += 4;
        }
        if(fourArr[i][2] == "("){
            currentMove += 2;
        }
        if(fourArr[i][3] == "("){
            currentMove++;
        } 
        netMoveFour += currentMove;
    }else if(fourArr[i][0] == ")"){
        if(fourArr[i][1] == "("){
            currentMove += 4;
        }
        if(fourArr[i][2] == "("){
            currentMove += 2;
        }
        if(fourArr[i][3] == "("){
            currentMove++;
        } 
        netMoveFour -= currentMove;
    }
}
if(netMoveFour > 0){
    console.log("The train is " + netMoveFour + " stations west");
}else if(netMoveFour < 0){
    console.log("The train is " + Math.abs(netMoveFour) + " stations east");
}